17  class stimulus_generator extends uvm_object; 
18  	`uvm_object_utils(stimulus_generator)
19  	//
20  	axi_transaction #(DW,AW1) w_trans_q [$];
21  	axi_transaction #(DW,AW1) r_trans_q [$];
22  	//mailbox #(axi_transaction #(DW,AW1)) r_mb;
23  	axi_transaction#(DW,AW1) w_trans, r_trans, tmp;
