35  	virtual task generate_wr_item();
36  		`uvm_info(get_type_name(), $sformatf("w_test_nums = %0d", w_test_nums), UVM_LOW);
37  		start_id = 0;
38  	 	repeat(w_test_nums) begin
39  		      w_trans = axi_transaction#(DW,AW1)::type_id::create("axi_wr_stimulus");
40  		      assert(w_trans.randomize()with{
41  				w_trans.burst inside {FIXED, INCR, WRAP};
42  				w_trans.id == start_id;
43  			})else 
44  		      $error("[WRITE]Randomization failed for Stimulus_Generator object!!! [addr=0x%08h burst=%s id=%0d]",
45  		      w_trans.addr, burst_name'(w_trans.burst), start_id);
46  		      //put generated item into queue
47  			w_trans_q.push_back(w_trans);
48  			//
49  			start_id++;
50  		end
51  	endtask
52  	//
53  	//
54  	virtual task generate_rd_item();
55  		`uvm_info(get_type_name(), $sformatf("r_test_nums = %0d", r_test_nums), UVM_LOW);
56  		start_id = 0;
57  	 	repeat(r_test_nums) begin
58  		      r_trans = axi_transaction#(DW,AW1)::type_id::create("axi_rd_stimulus");
59  		      assert(r_trans.randomize()with{
60  				r_trans.burst inside {FIXED, INCR, WRAP};
61  				r_trans.id == start_id;
62  			})else 
63  		      $error("[READ]Randomization failed for Stimulus_Generator object!!! [addr=0x%08h burst=%s id=%0d]",
64  		      r_trans.addr, burst_name'(r_trans.burst), start_id);
65  		      //put generated item into queue
66  			r_trans_q.push_back(r_trans);
67  			//
68  			start_id++;
69  		end
70  	endtask
71  	//
72  	//
73  	virtual task clone_wr_into_rd();
74  		idx = 0;
75  		`uvm_info(get_type_name(), $sformatf("r_test_nums = %0d", r_test_nums), UVM_LOW);
76  	 	repeat(r_test_nums) begin
77  		      tmp = axi_transaction#(DW,AW1)::type_id::create("tmp_stimulus");
78  			if(w_trans_q.size() == 0) begin
79  				if(idx == 0) begin
80  					`uvm_warning(get_type_name(), $sformatf("[CLONE_WR_INTO_RD]: EMPTY w_trans_q!!!"))
81  				end
82  				break;
83  			end
84  			//
85  			tmp = w_trans_q[idx];//access item without removing it from queue 
86  			$cast(r_trans, tmp.clone());
87  			r_trans_q.push_back(r_trans);
88  			idx++;
89  		end
90  	endtask	
