142  	//
143  	//randomize write data again
144  	//
145  	virtual task re_randomize_data();
146  		if(w_trans_q.size() == 0) begin
147  			`uvm_warning(get_type_name(), "UNAVAILABLE W_TRANS in enable_data_randomize mode!!!");
148  			return;
149  		end
150  		else begin
151  			foreach(w_trans_q[idx]) begin
152  				`uvm_info(get_type_name(), "BEFORE RE_RANDOMIZE", UVM_LOW);
153  				//w_trans_q[idx].print();
154  				//
155  				w_trans_q[idx].id = w_trans_q.size() + 1'd1;
156  				foreach(w_trans_q[idx].data[i]) begin
157  					w_trans_q[idx].data[i] = $urandom();
158  					//using solver to randomize can lead
159  					//--to failed randomization in case of
160  					//--large data array (>200 elements) 
161  					//--therefore, we should use $urandom()
162  				end
163  				`uvm_info(get_type_name(), "AFTER RE_RANDOMIZE", UVM_LOW);
164  				//w_trans_q[idx].print();
165  			end	
166  			`uvm_info(get_type_name(), $sformatf("succeed to re_randomize wdata!!!HAHAHA"), UVM_LOW);
167  		end
168  	endtask
169  endclass
