174  //--------------------------------AXI MASTER WRITE----------------------------------
175  //
176  class AxiMasterWriteSeq #(DW, AW1) extends uvm_sequence#(axi_transaction #(DW,AW1));
177      //register UVM factory
178      `uvm_object_param_utils(AxiMasterWriteSeq#(DW, AW1))
179    
180      // Variables
181      logic [7:0] start_id;
182      int idx;
183      //
184      axi_transaction #(DW, AW1) axi_wr_trans;
185      //
186      stimulus_generator stimulus_gen_ref;
187  
188      // Constructor
189      function new (string name = "AxiMasterWriteSeq");
190          super.new(name);
191        axi_wr_trans = axi_transaction#(DW,AW1)::type_id::create("axi_wr_trans");
192      endfunction
193  
194      extern virtual task body();
195  
196  endclass
197    
198  //main tasks in sequence
199      
200  //-- body()
201  task AxiMasterWriteSeq::body();
202  `uvm_info(get_type_name(), $sformatf("w_test_nums = %0d", stimulus_gen_ref.w_test_nums), UVM_LOW);
203  	idx = 0;
204      repeat(stimulus_gen_ref.w_test_nums) begin
205        // wait_for_grant();
206        //send_request(axi_wr_trans); //must pair wait_for_grant()
207        if(stimulus_gen_ref.w_trans_q.size() > 0) begin
208  		//`uvm_info(get_type_name(), $sformatf("[before] reading queue!!!"), UVM_LOW);
209  	      axi_wr_trans = stimulus_gen_ref.w_trans_q[idx];
210  		//`uvm_info(get_type_name(), $sformatf("[after] awid = %0d", axi_wr_trans.id), UVM_LOW);
211  	      //send item to sequencer
212  	      start_item(axi_wr_trans); //block until get_next_item() from driver
213  	      axi_wr_trans.set_bytes_in_beat();
214  	      finish_item(axi_wr_trans);
215  	      idx++;
216        end
217        else begin
218  		`uvm_fatal(get_type_name(), $sformatf("wr_stimulus not available!!!"))
219        end
220        //#(`CLK_CYCLE);
221      end
222  endtask
223  //
224  //--------------AXI MASTER READ-------------------------
225  //
226  class AxiMasterReadSeq#(DW,AW1) extends uvm_sequence#(axi_transaction #(DW,AW1));
227    `uvm_object_param_utils(AxiMasterReadSeq#(DW,AW1))
228    //
229      bit reconfigure_id;
230      logic [7:0] start_id; 
231      logic [7:0] r_id;
232      int idx_q;
233      //
234      axi_transaction #(DW,AW1) axi_rd_trans;
235      stimulus_generator stimulus_gen_ref;
236  
237      function new(string name = "read transaction");
238          super.new(name);
239  	      axi_rd_trans = axi_transaction#(DW,AW1)::type_id::create("axi_rd_trans");
240      endfunction 
241      //
242      extern virtual task body();
243  endclass
244  task AxiMasterReadSeq::body();
245  	r_id = 0;
246  	idx_q = 0;
247  	`uvm_info(get_type_name(), $sformatf("r_test_nums = %0d", stimulus_gen_ref.r_test_nums), UVM_LOW);
248  	repeat(stimulus_gen_ref.r_test_nums) begin
249  	      //stimulus_gen_ref.r_mb.get(tmp);
250  	      //axi_rd_trans.copy(tmp); 
251  	      //start_item does not recognize mailbox's element; therefore, we use copy() method to create new object
252  	      //
253  	      axi_rd_trans = stimulus_gen_ref.r_trans_q[idx_q];
254  	      //send item to sequencer
255  		`uvm_info(get_type_name(), $sformatf("[START]axi_rd_trans!!!"), UVM_HIGH);
256  	      start_item(axi_rd_trans);
257  	      axi_rd_trans.set_bytes_in_beat();
258  	      if(reconfigure_id) begin
259  		      axi_rd_trans.set_id(r_id);
260  	      end
261  	      axi_rd_trans.set_aligned_addr();
262  	      //item sent to driver done
263  	      finish_item(axi_rd_trans);
264  		`uvm_info(get_type_name(), $sformatf("[DONE]axi_rd_trans!!!"), UVM_HIGH);
265  	      r_id++;
266  	      idx_q++;
267  	end
268  endtask
