tt.pre:
1  //
2  //
3  //
4    //overall operation mechanism
5    //(R1) granting read --write waits -> grant write for next time
6    //(R2) granting read --ONLY read waits OR no request -> ... read ...
7    //(W1) granting write --read waits -> ... read ...
8    //(W2) granting write--ONLY write waits OR no request -> ... write ...
9  module arbiter(
10  	aclk,
11  	aresetn,
12  	sfifo_ar_empty_i,
13  	sfifo_aw_empty_i,
14  	burst_done_i,
15  	burst_go_i,
16  	//
17  	used_grant_o
18  );
19  //*****************************************************************
20  //ports
21  //*****************************************************************
22    input logic aclk;
23    input logic aresetn;
24    input logic sfifo_ar_empty_i;
25    input logic sfifo_aw_empty_i;
26    input logic burst_done_i;
27    input logic burst_go_i;
28    output logic [1:0] used_grant_o;
29  //*****************************************************************
30  //variables
31  //*****************************************************************
32    logic [1:0] nextSel;
33    logic [1:0] nextGrant;
34    logic [1:0] current_grant;
35    logic rd_req_avail, wr_req_avail;
36    logic update;
37    //
38    //output assignment
39    assign used_grant_o = current_grant;
40    //
41    assign rd_req_avail = ~sfifo_ar_empty_i;
42    assign wr_req_avail = ~sfifo_aw_empty_i;
43    //
44    //nextSel0
45    always_comb begin
46      if(current_grant[0])
47  	  nextSel[0] = 1'b1;
48  	else if(rd_req_avail)
49  	  nextSel[0] = 1'b0;
50  	else
51  	  nextSel[0] = 1'b1;
52    end
53    //nextSel1
54    always_comb begin
55      if(current_grant[1])
56  	  nextSel[1] = 1'b1;
57  	else if(wr_req_avail)
58  	  nextSel[1] = 1'b0;
59  	else
60  	  nextSel[1] = 1'b1;
61    end
62    //nextGrant[1]
63    always_comb begin
64      if(~nextSel[0])
65  	  nextGrant[1] = 1'b0;
66  	else if(wr_req_avail) //	R1
67  	  nextGrant[1] = 1'b1;
68  	else
69  	  nextGrant[1] = current_grant[1]; //R2
70    end
71    //nextGrant[0]
72    always_comb begin
73      if(~nextSel[1])
74  	  nextGrant[0] = 1'b0;
75  	else if(rd_req_avail) //W1
76  	  nextGrant[0] = 1'b1;
77  	else
78  	  nextGrant[0] = current_grant[0]; //W2
79    end
80  //
81  //current_grant
82  //
83  assign update = (burst_go_i ? 1'b0 : (current_grant[0] ? (wr_req_avail & sfifo_ar_empty_i) : (rd_req_avail & sfifo_aw_empty_i)))
84  | burst_done_i;
85  //
86  always_ff @(posedge aclk, negedge aresetn) begin
87  	if(~aresetn)
88  		current_grant[1:0] <= 2'b01;
89  	else if(update)
90  		current_grant[1:0] <= nextGrant[1:0];
91  	else
92  		current_grant[1:0] <= current_grant[1:0];	  
93  end
94  //
95  //
96  //
97  endmodule
