4  interface axi_intf(input logic aclk);
5    parameter DW = 32;
6    parameter AW1 = 32;
7    //axi address channel
8    logic aresetn;
9    logic awvalid;
10    logic [7:0] awid;
11    logic [7:0] awlen;
12    logic [1:0] awburst;
13    logic [2:0] awsize;
14    logic [AW1-1:0] awaddr;
15    logic awready;
16    logic [2:0] awprot;
17  
18    //axi write data channel
19    logic [DW-1:0] wdata;
20    logic [3:0] wstrb;
21    logic wvalid;
22    logic wlast;
23    logic wready;
24  
25    //axi write response channel
26    logic [7:0] bid;
27    logic [1:0] bresp;
28    logic bvalid;
29    logic bready;
30  
31    //axi read address channel
32    logic arvalid;
33    logic [7:0] arid;
34    logic [7:0] arlen;
35    logic [1:0] arburst;
36    logic [2:0] arsize;
37    logic [AW1-1:0] araddr;
38    logic arready;
39    logic [2:0] arprot;
40  
41    //axi read data channel
42    logic [7:0] rid;
43    logic [DW-1:0] rdata;
44    logic rvalid;
45    logic rlast;
46    logic rready;
47    logic [1:0] rresp;
48  //AXI driver
49  clocking m_drv_cb @(posedge aclk);
50   // default input #1 output #1;
51    input awready, wready, bid, bresp, bvalid, arready, rdata, rid, rvalid, rlast, rresp;
52    output awvalid, awid, awlen, awburst, awsize, awaddr, awprot, wdata, wstrb, wvalid, wlast,bready, 
53            arvalid, arid, arlen, arburst, arsize, araddr, arprot, rready;
54  endclocking
55  
56    //AXI monitor
57  clocking m_mon_cb @(posedge aclk);
58    //default input #1;
59    input awready, wready, bid, bresp, bvalid, arready, rdata, rid, rvalid, rlast, rresp,
60          awvalid, awid, awlen, awburst, awsize, awaddr, wdata, wvalid, wstrb, wlast,bready, 
61          arvalid, arid, arlen, arburst, arsize, araddr, rready;
62  endclocking
63    //
64    //open file
65    `ifdef PRINT_TO_VIF_SVA_FILE
66  	  string test_case;
67    string sim_result_path;
68    int axi_log_file;
69    //
70    initial begin
71      //
72      if (!$value$plusargs("UVM_TESTNAME=%s", test_case)) begin
73        test_case = "DEFAULT";
74      end
75      $sformat(sim_result_path, "../SIM_RESULT/%0dSLAVE/%s", `SLAVE_CNT, test_case);
76      axi_log_file = $fopen($sformatf("%s/VIF_SVA/axi_error.log", sim_result_path), "a"); // "w" = overwrite, "a" = append
77      if(axi_log_file == 0) begin
78        $display("ERROR: Could not open axi_error.log");
79      end
80    end
81    `endif
82    //
83    task wait_for_reset();
84      wait(aresetn == 1'b0);
85    endtask
86    //
87    task wait_RisingEdge_reset();
88      @(posedge aresetn);
89    endtask
90    //
91    task wait_FallingEdge_reset();
92      @(negedge aresetn);
93    endtask
94    //
95    function logic aresetn_value();
96      return aresetn;
97    endfunction
98    //
99    //CRITICAL NOTE: as using @(m_mon_cb), we must use m_mon_cb."signal" otherwise missed signal will occur
100    //--obviously bvalid && bready -> false
101    //
102    task one_write_req_done();
103      // @(posedge aclk iff bvalid && bready);
104      @(m_mon_cb iff m_mon_cb.bvalid && m_mon_cb.bready);
105    endtask
106    //
107    task one_read_req_done();
108      // @(posedge aclk iff (rvalid & rlast & rready));
109      @(m_mon_cb iff (m_mon_cb.rvalid && m_mon_cb.rlast && m_mon_cb.rready)); 
110    endtask
111    //****************************************************************************************************
112    //-------------------------------------AXI PROTOCOL SVA
113    //****************************************************************************************************
114  
115    //(0.1) REQ RESET ASSERTION
116    //DEFINE
117    property reset_all_reqsignal;
118  	@(posedge aclk)
119  	(!aresetn) |=> (awvalid == 0 && arvalid == 0 && awready == 1 && arready == 1);
120    endproperty
121    //DO
122    assert property (reset_all_reqsignal)
123    else begin
124      `ifdef PRINT_TO_VIF_SVA_FILE
125      $fdisplay(axi_log_file, "[RESET_REQSIGNAL][%0t ns]: All req signals are not properly reset!!!", $time);
126      `else
127      $error("[RESET_REQSIGNAL]: All req signals are not properly reset!!!");
128      `endif
129    end
130    //(0.2) DATA RESET ASSERTION
131    //DEFINE
132    property reset_all_datasignal;
133  	@(posedge aclk)
134  	(!aresetn) |=> (wvalid == 0 && rvalid == 0 && wlast == 0 && rlast == 0 && wready == 1 && rready == 0);
135    endproperty
136    //DO
137    assert property (reset_all_datasignal)
138    else begin
139      `ifdef PRINT_TO_VIF_SVA_FILE
140      $fdisplay(axi_log_file, "[RESET_DATASIGNAL][%0t ns]: All data signals are not properly reset!!!", $time);
141      `else 
142      $error("[RESET_DATASIGNAL]: All data signals are not properly reset!!!");
143      `endif
144    end
145    //(0.3) RESP RESET ASSERTION
146    //DEFINE
147    property reset_all_respsignal;
148  	@(posedge aclk)
149  	(!aresetn) |=> (bvalid == 0 && bready == 0 && bresp == 0);
150    endproperty
151    //DO
152    assert property (reset_all_respsignal)
153    else begin
154      `ifdef PRINT_TO_VIF_SVA_FILE
155      $fdisplay(axi_log_file, "[RESET_RESPSIGNAL][%0t ns]: All resp signals are not properly reset!!!", $time);
156      `else
157      $error("[RESET_RESPSIGNAL]: All resp signals are not properly reset!!!");
158      `endif 
159    end
160    //**************************************************************************************************
161    //*************************************ASSERTION WITH RESET = 1*************************************
162    //(1)
163    //DEFINE
164    property aw_handshake;
165      @(posedge aclk) disable iff (!aresetn)
166        awvalid |-> strong(awvalid[*1:$] intersect awready[->1]);
167    endproperty
168    //DO
169    assert property (aw_handshake)
170    else begin
171      `ifdef PRINT_TO_VIF_SVA_FILE
172      $fdisplay(axi_log_file, "[AW_HANDSHAKE][%0t ns]: There is no handshake in AW channel!!!", $time);
173      `else
174      $error("[AW_HANDSHAKE]: There is no handshake in AW channel!!!");
175      `endif
176    end
177  
178    //***************************************REUSABLE ASSERTION*************************************
179    //define start_of_burst sequence for future reusability
180    //
181  //sequence start_of_burst(valid, ready, last);
182     //$rose(valid && ready && !last);
183  //endsequence
184    //***************************************W CHANNEL ASSERTION*************************************
185    //(2)
186    //2.1
187    //DEFINE
188    property w_handshake;
189      @(posedge aclk) disable iff (!aresetn)
190        wvalid |-> strong(wvalid[*1:$] intersect wready[->1]);
191    endproperty
192    //DO
193    assert property (w_handshake)
194    else begin
195      `ifdef PRINT_TO_VIF_SVA_FILE
196      $fdisplay(axi_log_file, "[W_HANDSHAKE][%0t ns]: There is no handshake in W channel!!!", $time);
197      `else
198      $error("[W_HANDSHAKE]: There is no handshake in W channel!!!");
199      `endif
200    end
201    ////2.2
202    ////DEFINE
203    //property full_wdata;
204  	//@(posedge aclk) disable iff (!aresetn)
205  	 //start_of_burst(wvalid, wready, wlast) |-> (wvalid & wready)[=1:255] ##1 (wvalid & wlast);
206  	 ////using non-consecutive operator "=1:255" => wvalid & wlast could be
207  	 ////active at any moment in the future
208    //endproperty
209    ////DO
210    //assert property (full_wdata)
211    //else begin
212      //`ifdef PRINT_TO_VIF_SVA_FILE
213      //$fdisplay(axi_log_file, "[FULL_WDATA][%0t ns]: There is no complete transaction in W channel!!!", $time);
214      //`else
215      //$error("[FULL_WDATA]: There is no complete transaction in W channel!!!");
216      //`endif
217    //end
218    //(3)
219    //DEFINE
220    property b_handshake;
221      @(posedge aclk) disable iff (!aresetn)
222      	bvalid |-> first_match(bvalid[*1:$] ##0 bready);
223        //$rose(bvalid) |-> strong(bvalid[*1:$] intersect bready[->1]);
224    endproperty
225    //DO
226    assert property (b_handshake)
227    else begin
228      `ifdef PRINT_TO_VIF_SVA_FILE
229      $fdisplay(axi_log_file, "[B_HANDSHAKE][%0t ns]: There is no handshake in B channel!!!", $time);
230      `else
231      $error("[B_HANDSHAKE]: There is no handshake in B channel!!!");
232      `endif
233    end
234    //**************************************************************************************
235    //----------------------------READ SVA HANDLE
236    //**************************************************************************************
237    //(1)
238    //DEFINE
239    property ar_handshake;
240      @(posedge aclk) disable iff (!aresetn)
241    	arvalid |-> strong(arvalid[*1:$] intersect arready[->1]);
242    endproperty
243    //DO
244    assert property (ar_handshake)
245    else begin
246      `ifdef PRINT_TO_VIF_SVA_FILE
247      $fdisplay(axi_log_file, "[AR_HANDSHAKE][%0t ns]: There is no handshake in AR channel!!!", $time);
248      `else
249      $error("[AR_HANDSHAKE]: There is no handshake in AR channel!!!");
250      `endif
251    end
252    //2
253    //***************************************R CHANNEL ASSERTION*************************************
254    //(2.1)
255    //DEFINE
256    property r_handshake;
257      @(posedge aclk) disable iff (!aresetn)
258        rvalid |-> strong(rvalid[*1:$] intersect rready[->1]);
259    endproperty
260    //DO
261    assert property (r_handshake)
262    else begin
263      `ifdef PRINT_TO_VIF_SVA_FILE
264      $fdisplay(axi_log_file, "[R_HANDSHAKE][%0t ns]: There is no handshake in R channel!!!", $time);
265      `else
266      $error("[R_HANDSHAKE]: There is no handshake in R channel!!!");
267      `endif
268    end
269  
270    //2.2
271  
272    ////DEFINE
273    //property full_rdata;
274  	//@(posedge aclk) disable iff (!aresetn)
275  	//start_of_burst(rvalid, rready, rlast) |-> (rvalid & rready)[=1:255] ##1 (rvalid & rlast);
276    //endproperty
277    ////DO
278    //assert property (full_rdata) 
279    //else begin
280      //`ifdef PRINT_TO_VIF_SVA_FILE
281      //$fdisplay(axi_log_file, "[FULL_RDATA][%0t ns]: There is NO complete transaction in R channel!!!", $time);
282      //`else
283      //$error("[FULL_RDATA]: There is no complete transaction in R channel!!!");
284      //`endif
285    //end   
286  
287    //
288    //close file
289    //
290    `ifdef PRINT_TO_VIF_SVA_FILE
291    final begin
292      if(axi_log_file == 1) begin
293       $fclose(axi_log_file);
294      end
295    end
296    `endif
297  endinterface
