300  //
301  interface apb_intf (input logic pclk);
302  parameter DW = 32;
303  parameter AW2 = 32;
304  //apb channel
305    logic presetn;
306    logic [`SLAVE_CNT-1:0] psel;
307    logic penable;
308    logic pwrite;
309    logic [2:0] pprot;
310    logic [3:0] pstrb;
311    logic [AW2-1:0] paddr;
312    logic [DW-1:0] pwdata;
313    logic [`SLAVE_CNT-1:0][DW-1:0] prdata;
314    logic [`SLAVE_CNT-1:0] pready;
315    logic [`SLAVE_CNT-1:0] pslverr;
316    //
317    //
318    //Open file for logging
319    //
320    `ifdef PRINT_TO_VIF_SVA_FILE
321  	  string test_case;
322    string sim_result_path; 
323    integer apb_log_fh;
324    initial begin
325      if (!$value$plusargs("UVM_TESTNAME=%s", test_case)) begin
326        test_case = "DEFAULT";
327      end
328      $sformat(sim_result_path, "../SIM_RESULT/%0dSLAVE/%s", `SLAVE_CNT, test_case);
329      apb_log_fh = $fopen($sformatf("%s/VIF_SVA/apb_error.log", sim_result_path), "a"); // "w" = overwrite, "a" = append
330      if (apb_log_fh == 0) begin
331        $display("ERROR: Could not open apb_error.log!!!");
332      end
333    end
334    `endif
335    // =====================================
336    // SVA Protocol Checks
337    // =====================================
338    genvar i;
339      generate;
340      for(i = 0; i < `SLAVE_CNT; i++) begin : apb_slave_checks
341        //(1)
342        property psel_onehot;
343          @(posedge pclk) disable iff (!presetn)
344            $onehot0(psel);
345        endproperty
346        assert property (psel_onehot)
347        else begin
348          `ifdef PRINT_TO_VIF_SVA_FILE
349          $fdisplay(apb_log_fh, "PSEL_SVA: multiple slaves selected at the same time!!! Value = %0b @time=%0t ns", psel, $time);
350          `else
351          $error("PSEL_SVA: multiple slaves selected at the same time!!! Value = %0b", psel);
352          `endif
353        end
354        //reset all signals
355        //DEFINE
356        property reset_all_signals;
357          @(posedge pclk) disable iff (!presetn)
358          (presetn == 0) |=> (psel == 0 && penable == 0 && paddr == 0);
359        endproperty
360        //DO
361        assert property (reset_all_signals)
362        else begin
363          `ifdef PRINT_TO_VIF_SVA_FILE
364          $fdisplay(apb_log_fh, "[RESEL_ALL_SIGNALS]: all APB signals are not properly reset!!! @time=%0t ns", $time);
365          `else
366          $error("[RESEL_ALL_SIGNALS]: all APB signals are not properly reset!!!");
367          `endif
368        end
369        //
370        //(2)
371        // access_phase: if psel[i] and !penable, then penable must go high next cycle
372        property access_phase;
373          @(posedge pclk) disable iff (!presetn)
374            (psel[i] && !penable) |=> penable;
375        endproperty
376        assert property(access_phase)
377        else begin
378          `ifdef PRINT_TO_VIF_SVA_FILE
379          $fdisplay(apb_log_fh, $sformatf("APB_SVA[%0d]: penable not asserted in ACCESS phase @time=%0t ns", i, $time));
380          `else
381          $error("APB[%0d]: penable not asserted in ACCESS phase", i);
382          `endif
383        end
384        //
385        //(3)
386        // Transfer Completion only when pready[i] is asserted
387        //DEFINE
388        property complete_with_pready;
389          @(posedge pclk) disable iff (!presetn)
390            (psel[i] && penable && ~pready[i]) |=> penable;
391        endproperty
392        //DO
393        assert property(complete_with_pready)
394        else begin
395          `ifdef PRINT_TO_VIF_SVA_FILE
396          $fdisplay(apb_log_fh, $sformatf("APB_SVA[%0d]: Transfer ended without pready @time=%0t ns", i, $time));
397          `else
398          $error("APB_SVA[%0d]: Transfer ended without pready", i);
399          `endif
400        end
401        //
402        //(4)
403        //
404        //DEFINE
405        property penable_deassert;
406          @(posedge pclk) disable iff (!presetn)
407            (psel[i] && penable && pready[i]) |=> !penable;
408        endproperty
409        //DO
410        assert property(penable_deassert)
411        else begin
412          `ifdef PRINT_TO_VIF_SVA_FILE
413          $fdisplay(apb_log_fh, $sformatf("APB_SVA[%0d]: Penable also ASSERT as pready is high @time=%0t ns", i, $time));
414          `else
415          $error("APB[%0d]: Penable also ASSERT as pready is high", i);
416          `endif
417        end
418      end
419    endgenerate
420    clocking s_drv_cb @(posedge pclk);
421        input psel, penable, pwrite, pstrb, paddr, pwdata, pprot;
422        output prdata, pready, pslverr;
423    endclocking
424  
425    //
426    clocking s_mon_cb @(posedge pclk);
427        input psel, penable, pwrite, paddr, pwdata, pstrb, prdata, pready, pslverr, pprot;
428    endclocking
429  
430    //
431    //close file
432    //
433    `ifdef PRINT_TO_VIF_SVA_FILE
434    final begin
435      if(apb_log_fh == 1) begin
436       $fclose(apb_log_fh);
437      end
438    end
439    `endif
440  endinterface
