tt.pre:
1  //
2  //
3  //
4  module apb_master(
5  	pclk,
6  	preset_n,
7  	//
8  	//control signal
9  	//
10  	trans_go_i,
11  	next_transfer_rdy_i,
12  	end_of_burst_i,
13  	grant_to_write_i,
14  	wstrb_to_apb_i,	
15  	wdata_to_apb_i,	
16  	start_addr_i,
17  	len_of_burst_i,
18  	size_of_transfer_i,
19  	used_burst_i,
20  	used_prot_i,
21  	//APB interface
22  	paddr,
23  	pprot,
24  	psel,
25  	penable,
26  	pwrite,
27  	pwdata,
28  	pstrb,
29  	pready,
30  	prdata,
31  	pslverr,
32  //send to AXI transaction controller
33  	prdataX_o,
34  	pslverrX_o,
35  	//control outputs
36  	write_to_rd_sfifo_o,
37  	read_from_wd_sfifo_o,
38  	set_up_phase_o,
39  	beat_cnt_incr_o,
40  	dec_error_o,
41  	latch_resp_o
42  	//
43  	//
44  	//
45  );
46  //*********************************************************
47  //PORTS
48  //*********************************************************
49  input logic                    pclk;
50  input logic                    preset_n;
51  //
52  input logic trans_go_i;
53  input logic next_transfer_rdy_i;
54  input logic end_of_burst_i;
55  input logic grant_to_write_i;
56  input logic [3:0]	wstrb_to_apb_i;
57  input logic [31:0]	wdata_to_apb_i;	
58  input logic [31:0]	start_addr_i;
59  input logic [7:0]	len_of_burst_i;
60  input logic [2:0] 	size_of_transfer_i;
61  input logic [1:0]	used_burst_i;
62  input logic [2:0]	used_prot_i;
63  //
64  output logic [31:0]             paddr;
65  output logic [2:0]              pprot;
66  output logic [`SLAVE_CNT-1:0]   psel;
67  output logic                    penable;
68  output logic                    pwrite;
69  output logic [31:0]             pwdata;
70  output logic [3:0]              pstrb;
71  input  logic [`SLAVE_CNT-1:0][31:0] prdata;
72  input  logic [`SLAVE_CNT-1:0]   pready;
73  input  logic [`SLAVE_CNT-1:0]   pslverr;
74  //send to AXI transaction controller
75  output logic [31:0] prdataX_o;
76  output logic pslverrX_o;
77  //control outputs
78  output logic	write_to_rd_sfifo_o;
79  output logic	read_from_wd_sfifo_o;
80  output logic 	set_up_phase_o;
81  output logic	beat_cnt_incr_o;
82  output logic 	dec_error_o;
83  output logic 	latch_resp_o;
84  //*********************************************************
85  //INTERNAL VARIABLES
86  //*********************************************************
87  //psel register
88   logic false_psel_reg, true_psel_reg;
89  //apb transfer address
90    logic [31:0]                    next_addr_for_incr;
91    logic [31:0]                    next_addr_for_wrap;
92    logic [2:0]                     bit_num;
93    logic [2:0]                     bit3Addr;
94    logic [3:0]                     bit4Addr;
95    logic [4:0]                     bit5Addr;
96    logic [5:0]                     bit6Addr;
97    //abp address control
98    logic addr_incr_en;
99    logic begin_transfer;
100    //DECODER signals
101    logic [`SLAVE_CNT-1:0] true_psel;
102    logic false_psel;
103    logic preadyX;
104    logic error_of_decoder;
105    //APB FMS output
106    logic invalid_psel;
107    //APB state
108  //APB state machine
109  typedef enum logic [1:0] {P_IDLE, SETUP, ACCESS} apb_st;
110    apb_st apb_cs, apb_ns;
111    //
112    //APB state regs
113    //
114    always_ff @(posedge pclk, negedge preset_n) begin
115      if(~preset_n)
116  	  apb_cs[1:0] <= P_IDLE;
117  	else
118  	  apb_cs[1:0] <= apb_ns[1:0];
119    end
120  //
121  //
122  //
123    always_comb begin
124  	apb_ns = apb_cs;
125  	pprot = 0;
126  	psel = 0;
127  	invalid_psel = 0;
128  	penable = 0;
129  	pwrite = 0;
130  	pwdata = 0;
131  	pstrb = 0;
132  	set_up_phase_o = 1'b0;
133  	write_to_rd_sfifo_o = 1'b0;
134  	latch_resp_o = 1'b0;
135  	beat_cnt_incr_o = 1'b0;
136  	read_from_wd_sfifo_o = 1'b0;
137  	addr_incr_en = 1'b0;
138  	//
139      case(apb_cs[1:0])
140  		P_IDLE: begin
141  			if(next_transfer_rdy_i) begin
142  				apb_ns[1:0] = SETUP;
143  			end
144  			else
145  			apb_ns[1:0] = P_IDLE;
146  		end
147  		SETUP: begin
148  			apb_ns[1:0] = ACCESS;
149  			psel = true_psel_reg;
150  			invalid_psel = false_psel_reg;
151  			penable = 1'b0;
152  			pwrite = (grant_to_write_i) ? 1'b1: 1'b0;
153  			pwdata = (grant_to_write_i) ? wdata_to_apb_i[31:0] : 32'd0;
154  		    pstrb[3:0] = (grant_to_write_i) ? wstrb_to_apb_i[3:0] : 4'h0;
155  		    	set_up_phase_o = 1'b1;
156  		end
157  		ACCESS: begin
158  			pprot[2:0] = used_prot_i;
159  			psel = true_psel_reg;
160  			invalid_psel = false_psel_reg;
161  			penable = 1'b1;
162  			pwrite = (grant_to_write_i) ? 1'b1: 1'b0;
163  			pwdata = (grant_to_write_i) ? wdata_to_apb_i[31:0] : 32'd0;
164  		    pstrb[3:0] = (grant_to_write_i) ? wstrb_to_apb_i[3:0] : 4'h0;
165  			//
166  			if(preadyX) begin
167  				write_to_rd_sfifo_o = 1'b1;
168  				latch_resp_o = (grant_to_write_i) ? 1'b1 : 1'b0;
169  				beat_cnt_incr_o = 1'b1;
170  				read_from_wd_sfifo_o = 1'b1;
171  				//
172  				if(next_transfer_rdy_i) begin
173  					apb_ns[1:0] = SETUP;
174  					addr_incr_en = 1'b1;
175  				end
176  				else begin
177  					apb_ns[1:0] = P_IDLE;
178  					addr_incr_en = end_of_burst_i ? 1'b0 : 1'b1;
179  				end
180  			end //end of preadyX
181  			else begin
182  				apb_ns[1:0] = ACCESS;
183  			end
184  		end
185  		default: begin
186  			apb_ns[1:0] = P_IDLE;
187  			pprot = 0;
188  			psel = 0;
189  			invalid_psel = 0;
190  			penable = 0;
191  			pwrite = 0;
192  			pwdata = 0;
193  			pstrb = 0;
194  			set_up_phase_o = 1'b0;
195  			write_to_rd_sfifo_o = 1'b0;
196  			latch_resp_o = 1'b0;
197  			beat_cnt_incr_o = 1'b0;
198  			read_from_wd_sfifo_o = 1'b0;
199  			addr_incr_en = 1'b0;
200  		end
201  	endcase
202    end
203  //
204  //psel register
205  //
206  always@(posedge pclk, negedge preset_n) begin
207   if(~preset_n) begin
208  	 true_psel_reg <= {`SLAVE_CNT{1'b0}};
209  	 false_psel_reg <= 1'b0;
210   end
211   else begin
212  	 true_psel_reg <= true_psel;
213  	 false_psel_reg <= false_psel;
214   end
215  end
216  //
217  //paddr
218  //
219  always_ff @(posedge pclk, negedge preset_n) begin
220  if(~preset_n) begin
221    paddr[31:0] <= 32'd0;
222  end
223  else begin
224  	if(true_psel) begin //valid_transfer
225  		if(begin_transfer) paddr[31:0] <= {start_addr_i[31:2], 2'b00};
226  		else begin
227  			if(addr_incr_en) begin
228  				case(used_burst_i[1:0])
229  				2'b00:  paddr[31:0] <= paddr[31:0]; //FIXED 
230  				2'b01:  begin //INCR
231  					paddr[31:0] <= next_addr_for_incr[31:0];
232  				end
233  				2'b10:  begin //WRAP
234  					paddr[31:0] <= next_addr_for_wrap[31:0];
235  				end
236  				default: paddr[31:0] <= 32'd0;
237  				endcase
238  			end //end of if addr_incr_en
239  			else begin
240  				paddr[31:0] <= paddr[31:0];
241  			end
242  		end
243  	end//end of valid_transfer
244  	else begin
245  		paddr[31:0] <= 32'd0;
246  	end
247  end//end of else presetn
248  end
249  //
250  assign next_addr_for_incr[31:0] = paddr[31:0] + 3'd4;
251      //
252    //bit_num
253    always_comb begin
254      case(len_of_burst_i[7:0])
255  	  8'd1:  bit_num[2:0] = 3'b011;
256  	  8'd3:  bit_num[2:0] = 3'b100;
257  	  8'd7:  bit_num[2:0] = 3'b101;
258  	  8'd15: bit_num[2:0] = 3'b110;
259  	  default: bit_num[2:0] = 3'bx;
260  	endcase
261    end
262    //bit3Addr, bit4Addr, bit5Addr, bit6Addr
263    always_comb begin
264      if(bit_num[2:0] == 3'b011)
265  	  bit3Addr[2:0] = paddr[2:0] + 3'd4;
266  	else
267  	  bit3Addr[2:0] = 3'd0;
268    end
269    always_comb begin
270      if(bit_num[2:0] == 3'b100)
271  	  bit4Addr[3:0] = paddr[3:0] + 3'd4;
272  	else
273  	  bit4Addr[3:0] = 4'd0;
274    end
275    always_comb begin
276      if(bit_num[2:0] == 3'b101)
277  	  bit5Addr[4:0] = paddr[4:0] + 3'd4;
278  	else
279  	  bit5Addr[4:0] = 5'd0;
280    end
281    always_comb begin
282      if(bit_num[2:0] == 3'b110)
283  	  bit6Addr[5:0] = paddr[5:0] + 3'd4;
284  	else
285  	  bit6Addr[5:0] = 6'd0;
286    end
287    //next_addr_for_wrap
288    always_comb begin
289      case(bit_num[2:0])
290  	  3'b011: next_addr_for_wrap[31:0] = {paddr[31:3], bit3Addr[2:0]};
291  	  3'b100: next_addr_for_wrap[31:0] = {paddr[31:4], bit4Addr[3:0]};
292  	  3'b101: next_addr_for_wrap[31:0] = {paddr[31:5], bit5Addr[4:0]};
293  	  3'b110: next_addr_for_wrap[31:0] = {paddr[31:6], bit6Addr[5:0]};
294  	  default: next_addr_for_wrap[31:0] = 32'bx;
295  	endcase
296    end
297    //dec error register
298    always_ff@(posedge pclk, negedge preset_n) begin
299  	  if(~preset_n) dec_error_o <= 1'b0;
300  	  else if(begin_transfer) dec_error_o <= error_of_decoder;
301    end
302    //
303  RiSiEdgeDetector RiSiEdgeDetector_trans_go(
304    .clk_i(pclk),
305    .rstn_i(preset_n),
306    .sign_i(trans_go_i),
307    .red_o(begin_transfer)  
308    //
309  );
310  //
311  //DECODER 
312  //
313  decoder decoder_inst(
314  	.start_addr_i(start_addr_i),
315  	.size_of_transfer_i(size_of_transfer_i),
316  	.used_burst_i(used_burst_i),
317  	//
318  	.invalid_psel_i(invalid_psel),
319  	.psel(psel),
320  	.pready(pready),
321  	.pslverr(pslverr),
322  	.prdata(prdata),
323  	//
324  	.preadyX_o(preadyX),
325  	.pslverrX_o(pslverrX_o),
326  	.prdataX_o(prdataX_o),
327   	.dec_error_o(error_of_decoder),
328  	.true_psel_o(true_psel),
329  	.false_psel_o(false_psel)
330  	//
331  	//
332  	//
333  );
334  
335  endmodule
