tt.pre:
1  //
2  //
3  //
4  module decoder(
5  	start_addr_i,
6  	size_of_transfer_i,
7  	used_burst_i,
8  	//
9  	invalid_psel_i,
10  	psel,
11  	pready,
12  	pslverr,
13  	prdata,
14  	//
15  	preadyX_o,
16  	pslverrX_o,
17  	prdataX_o,
18   	dec_error_o,
19  	true_psel_o,
20  	false_psel_o	
21  	//
22  );
23  //iclude parameter file
24  import parameter_pkg::*; 
25  //PORTS
26  input logic [31:0] start_addr_i;
27  input logic [2:0] size_of_transfer_i;
28  input logic [1:0] used_burst_i;
29  input logic invalid_psel_i;
30  input logic [`SLAVE_CNT-1:0] psel;
31  input logic [`SLAVE_CNT-1:0] pready;
32  input logic [`SLAVE_CNT-1:0] pslverr;
33  input logic [`SLAVE_CNT-1:0][31:0] prdata;
34  //
35  output logic dec_error_o;
36  output logic [`SLAVE_CNT-1:0] preadyX_o;
37  output logic [`SLAVE_CNT-1:0] pslverrX_o;
38  output logic [`SLAVE_CNT-1:0][31:0] prdataX_o;
39  output logic [`SLAVE_CNT-1:0] true_psel_o;
40  output logic false_psel_o;
41  //
42  //variables
43  logic bytes_in_transfer;
44  logic nonexist_transfer;
45  logic [`SLAVE_CNT-1:0] out_pready;
46  logic [`SLAVE_CNT-1:0] out_pslverr;
47  logic [`SLAVE_CNT-1:0][31:0] out_prdata;
48  //
49  assign bytes_in_transfer = 2**(size_of_transfer_i);
50  assign nonexist_transfer = (used_burst_i[1:0] == 2'b11) | (bytes_in_transfer != 8'd4); 
51  //
52  //psel for APB slave 
53  //
54  generate
55  if(`SLAVE_CNT >= 1) 
56    assign true_psel_o[0] = nonexist_transfer ? 1'b0 : ((start_addr_i[31:0] >= A_START_SLAVE0) & (start_addr_i[31:0] <= A_END_SLAVE0));
57  if(`SLAVE_CNT >= 2)
58    assign true_psel_o[1] = nonexist_transfer ? 1'b0 : ((start_addr_i[31:0] >= A_START_SLAVE1) & (start_addr_i[31:0] <= A_END_SLAVE1));
59  if(`SLAVE_CNT >= 3)
60    assign true_psel_o[2] = nonexist_transfer ? 1'b0 : ((start_addr_i[31:0] >= A_START_SLAVE2) & (start_addr_i[31:0] <= A_END_SLAVE2));
61  if(`SLAVE_CNT >= 4)
62    assign true_psel_o[3] = nonexist_transfer ? 1'b0 : ((start_addr_i[31:0] >= A_START_SLAVE3) & (start_addr_i[31:0] <= A_END_SLAVE3));
63  endgenerate
64  //
65  //this signal ensures ongoing transaction in case of non-existent address
66  //  
67  generate
68  if(`SLAVE_CNT == 1)
69    assign false_psel_o  = (start_addr_i[31:0] > A_END_SLAVE0);
70  if(`SLAVE_CNT == 2)
71    assign false_psel_o  = (start_addr_i[31:0] > A_END_SLAVE1);
72  if(`SLAVE_CNT == 3)
73    assign false_psel_o  = (start_addr_i[31:0] > A_END_SLAVE2);
74  if(`SLAVE_CNT == 4)
75    assign false_psel_o  = (start_addr_i[31:0] > A_END_SLAVE3);
76  endgenerate
77   //
78   //
79   //
80  generate
81  	if(`SLAVE_CNT == 1)
82  	  assign dec_error_o = (start_addr_i[31:0] > A_END_SLAVE0);
83  	else if(`SLAVE_CNT == 2)
84  	  assign dec_error_o = (start_addr_i[31:0] > A_END_SLAVE1);
85  	else if(`SLAVE_CNT == 3)
86  	  assign dec_error_o = (start_addr_i[31:0] > A_END_SLAVE2);
87  	else if(`SLAVE_CNT == 4)
88  	  assign dec_error_o = (start_addr_i[31:0] > A_END_SLAVE3);
89  endgenerate
90    //pslverrX, preadyX
91    assign preadyX_o  = |out_pready[`SLAVE_CNT-1:0] | invalid_psel_i | nonexist_transfer; 
92    assign pslverrX_o = |out_pslverr[`SLAVE_CNT-1:0]| invalid_psel_i | nonexist_transfer; 
93    generate
94      genvar i;
95  	for (i = 0; i <= `SLAVE_CNT-1; i = i + 1) begin: decPreadyAndPslverr
96  	  assign out_pready[i]  = psel[i] & pready[i];
97  	  assign out_pslverr[i] = psel[i] & pslverr[i];
98  	end
99    endgenerate
100    //prdataX
101    assign prdataX_o = out_prdata[`SLAVE_CNT-1];
102    assign out_prdata[0] = psel[0] ? prdata[0] : 32'd0;
103    //
104    generate
105      genvar j;
106  	for(j = 1; j <= `SLAVE_CNT-1; j = j + 1) begin: decPrdata
107  	  assign out_prdata[j] = psel[j] ? prdata[j] : out_prdata[j-1];
108  	end
109    endgenerate
110    //
111    //
112    //
113    endmodule
