1  module axi_cov();
2      logic aclk;
3      logic aresetn;
4      logic awvalid;
5      logic awready;
6      logic [7:0] awid;
7      logic [31:0] awaddr;
8      logic [7:0] awlen;
9      logic [2:0] awsize;
10      logic [1:0] awburst;
11      logic wvalid;
12      logic wready;
13      logic [31:0] wdata;
14      logic [3:0] wstrb;
15      logic wlast;
16      logic bvalid;
17      logic [1:0] bresp;
18      logic [7:0] bid;
19      logic bready;
20      //
21      logic arvalid;
22      logic arready;
23      logic [7:0] arid;
24      logic [31:0] araddr;
25      logic [7:0] arlen;
26      logic [2:0] arsize;
27      logic [1:0] arburst;
28      logic rvalid;
29      logic rready;
30      logic [31:0] rdata;
31      logic rlast;
32      logic [1:0] rresp;
33      logic [7:0] rid;
34      // parameter INST_NAME0   = "AXI_FUNCTIONAL_COVERAGE_PART";
35      // parameter INST_NAME1   = "AXI_FUNCTIONAL_COVERAGE_TOTAL";
36      //
37      //main covergroup
38      //
39      //--1: aw channel cg
40      covergroup axi_wreq_cg @(posedge aclk iff aresetn);
41          option.per_instance = 1;
42          //
43          awvalid_cp: coverpoint awvalid {
44              bins value_0 = {1'b0};
45              bins value_1 = {1'b1};
46          }
47          awready_cp: coverpoint awready {
48              bins value_0 = {1'b0};
49              bins value_1 = {1'b1};
50          }
51          awid_cp: coverpoint awid iff awvalid && awready;//{
52              //bins very_low = {[0:15]};
53              //bins mid0_low = {[16:31]};
54              //bins mid1_low = {[32:45]};
55              //bins low  = {[46:63]};
56              //bins mid  = {[64:127]};
57              //bins high = {[128:191]};
58              //bins top  = {[192:255]};
59          //}
60          awaddr_cp: coverpoint awaddr iff awvalid && awready{
61              wildcard bins aligned_addr = {32'b00000000_0000000?_????????_??????00};
62              wildcard bins unaligned_addr0 = {32'b00000000_0000000?_????????_???????1};
63              wildcard bins unaligned_addr1 = {32'b00000000_0000000?_????????_??????10};
64              //
65          }
66          len_cp : coverpoint awlen iff awvalid && awready{
67              bins wrap   = {1,3,7,15}; // spec-defined
68              bins rest   = {[0:255]};
69          }
70  
71          awburst_cp : coverpoint awburst iff awvalid && awready{
72              bins fixed = {2'b00};
73              bins incr  = {2'b01};
74              bins wrap  = {2'b10};
75              ignore_bins reserved = {2'b11};
76          }
77          //
78          //cross coverage
79          //
80          burst_x_len: cross awburst_cp, len_cp {
81              bins xy1 = binsof(awburst_cp.wrap) && binsof(len_cp.wrap);
82              bins xy2 = binsof(awburst_cp.fixed) && binsof(len_cp.rest);
83              bins xy3 = binsof(awburst_cp.incr) && binsof(len_cp.rest);
84          }
85          //
86          burst_x_addr: cross awburst_cp, awaddr_cp {
87              bins xy1 = binsof(awburst_cp.incr);
88              bins xy2 = binsof(awburst_cp.fixed) && binsof(awaddr_cp.aligned_addr);
89              bins xy3 = binsof(awburst_cp.wrap) && binsof(awaddr_cp.aligned_addr);
90              ignore_bins others = (binsof(awburst_cp.fixed) || binsof(awburst_cp.wrap)) && !binsof(awaddr_cp.aligned_addr);
91          }
92      endgroup
93      //--2: wdata channel cg
94      covergroup axi_wdata_cg @(posedge aclk iff aresetn);
95          option.per_instance = 1;
96          //
97          wvalid_cp: coverpoint wvalid {
98              bins value_0 = {1'b0};
99              bins value_1 = {1'b1};
100          }
101          wready_cp: coverpoint wready {
102              bins value_0 = {1'b0};
103              bins value_1 = {1'b1};
104          }
105          wstrb_cp: coverpoint wstrb iff wvalid && wready{
106              bins single = {4'h1, 4'h2, 4'h4, 4'h8};
107              bins half = {4'h3, 4'hC};
108              bins third = {4'h7, 4'hE};
109              bins full = {4'hf};
110              ignore_bins empty = {4'h0};
111          }
112          wlast_cp: coverpoint wlast iff wvalid && wready{
113              bins value_0 = {1'b0};
114              bins value_1 = {1'b1};
115          }
116      endgroup
117      //--3: bresp channel cg
118      covergroup axi_bresp_cg @(posedge aclk iff aresetn);
119          option.per_instance = 1;
120          //
121          bvalid_cp: coverpoint bvalid {
122              bins value_0 = {1'b0};
123              bins value_1 = {1'b1};
124          }
125          bready_cp: coverpoint bready {
126              bins value_0 = {1'b0};
127              bins value_1 = {1'b1};
128          }
129          bid_cp: coverpoint bid iff bvalid && bready; //{
130              //bins low  = {[0:63]};
131              //bins mid  = {[64:127]};
132              //bins high = {[128:191]};
133              //bins top  = {[192:255]};
134          //}
135          bresp_cp: coverpoint bresp iff bvalid && bready{
136              bins okay = {2'b00};  // OKAY response
137              ignore_bins exokay = {2'b01}; // EXOKAY response
138              bins slverr = {2'b10}; // SLVERR response
139              bins decerr = {2'b11}; // DECERR response
140          }
141      endgroup
142  
143      //--4: ar channel cg
144      covergroup axi_rreq_cg @(posedge aclk iff aresetn);
145          option.per_instance = 1;
146          //
147          arvalid_cp: coverpoint arvalid {
148              bins value_0 = {1'b0};
149              bins value_1 = {1'b1};
150          }
151          arready_cp: coverpoint arready {
152              bins value_0 = {1'b0};
153              bins value_1 = {1'b1};
154          }
155          arid_cp: coverpoint arid iff arvalid && arready;//{
156              //bins low  = {[0:63]};
157              //bins mid  = {[64:127]};
158              //bins high = {[128:191]};
159              //bins top  = {[192:255]};
160          //}
161          araddr_cp: coverpoint araddr iff arvalid && arready{
162              wildcard bins aligned_addr = {32'b00000000_0000000?_????????_??????00};
163              wildcard bins unaligned_addr0 = {32'b00000000_0000000?_????????_???????1};
164              wildcard bins unaligned_addr1 = {32'b00000000_0000000?_????????_??????10};
165          }
166          len_cp : coverpoint arlen iff arvalid && arready{
167              bins wrap   = {1,3,7,15}; // spec-defined
168              bins rest   = {[0:255]};
169          }
170  
171          arburst_cp : coverpoint arburst iff arvalid && arready{
172              bins fixed = {2'b00};
173              bins incr  = {2'b01};
174              bins wrap  = {2'b10};
175              ignore_bins reserved = {2'b11};
176          }
177          //
178          //cross coverage
179          //
180  	//--to adopt binsof to define expected coverage and use ignore_bins to
181  	//remove unnecessary or unwanted coverage 
182          burst_x_len: cross arburst_cp, len_cp {
183              bins xy1 = binsof(arburst_cp.wrap) && binsof(len_cp.wrap);
184              bins xy2 = binsof(arburst_cp.fixed) && binsof(len_cp.rest);
185              bins xy3 = binsof(arburst_cp.incr) && binsof(len_cp.rest);
186          }
187          //
188          burst_x_addr: cross arburst_cp, araddr_cp {
189              bins xy1 = binsof(arburst_cp.incr);
190              bins xy2 = binsof(arburst_cp.fixed) && binsof(araddr_cp.aligned_addr);
191              bins xy3 = binsof(arburst_cp.wrap) && binsof(araddr_cp.aligned_addr);
192              ignore_bins others = (binsof(arburst_cp.fixed) || binsof(arburst_cp.wrap)) && !binsof(araddr_cp.aligned_addr);
193          }
194      endgroup
195      
196      //--5: rdata channel cg
197      covergroup axi_rdata_cg @(posedge aclk iff aresetn);
198          option.per_instance = 1;
199          //
200          rvalid_cp: coverpoint rvalid {
201              bins value_0 = {1'b0};
202              bins value_1 = {1'b1};
203          }
204          rready_cp: coverpoint rready {
205              bins value_0 = {1'b0};
206              bins value_1 = {1'b1};
207          }
208          rlast_cp: coverpoint rlast iff(rvalid && rready){
209              bins value_0 = {1'b0};
210              bins value_1 = {1'b1};
211          }
212          rresp_cp: coverpoint rresp iff(rvalid && rready){
213              bins okay = {2'b00};  // OKAY response
214              ignore_bins exokay = {2'b01}; // EXOKAY response
215              bins slverr = {2'b10}; // SLVERR response
216              bins decerr = {2'b11}; // DECERR response
217          }
218          rid_cp: coverpoint rid iff(rvalid && rready);//{
219              //bins low  = {[0:63]};
220              //bins mid  = {[64:127]};
221              //bins high = {[128:191]};
222              //bins top  = {[192:255]};
223          //}
224      endgroup
225      // ------------------------------------------------------
226      // Instances
227      // ------------------------------------------------------
228      axi_wreq_cg   wreq_cov  = new();
229      axi_wdata_cg  wdata_cov = new();
230      axi_bresp_cg  bresp_cov = new();
231      axi_rreq_cg   rreq_cov  = new();
232      axi_rdata_cg  rdata_cov = new();
233  
