tt.pre:
1  //===================================================================================
2  // File name:	sfifo.v
3  // Project:	  RTL code generator
4  // Module:    Flexible synchronous FIFO
5  // Function:	Synchronous FIFO with the configuration parameters
6  //===================================================================================
7  
8  //All defines are used to configured the synchronous FIFO before synthesizing
9  `ifndef EMPTY_SIGNAL
10    `define EMPTY_SIGNAL
11  `endif
12  `ifndef FULL_SIGNAL
13    `define FULL_SIGNAL
14  `endif
15  // `define SET_LOW_EN
16  //`define SET_HIGH_EN
17  //`define ALMOST_EMPTY_SIGNAL
18  //`define ALMOST_FULL_SIGNAL
19  //`define OV_SIGNAL
20  //`define UD_SIGNAL
21  // `define OUTPUT_REG
22  //`define TWO_CLOCK
23  module sfiforeq (rst_n, wr, rd,
24                    `ifdef TWO_CLOCK
25                      clk_rd, clk_wr,
26                    `else
27                      clk,
28                    `endif
29                    `ifdef EMPTY_SIGNAL
30                      sfifo_empty,
31                    `endif
32                    `ifdef FULL_SIGNAL
33                      sfifo_full,
34                    `endif
35                    `ifdef SET_LOW_EN
36                      low_th,
37                    `endif
38                    `ifdef SET_HIGH_EN
39                      high_th,
40                    `endif
41                    `ifdef ALMOST_EMPTY_SIGNAL
42                      sfifo_almost_empty,
43                    `endif
44                    `ifdef ALMOST_FULL_SIGNAL
45                      sfifo_almost_full,
46                    `endif
47                    `ifdef OV_SIGNAL
48                      sfifo_ov,
49                    `endif
50                    `ifdef UD_SIGNAL
51                      sfifo_ud,
52                    `endif
53                      data_in, data_out);
54  //Parameters are used to set the capacity of FIFO
55  parameter DATA_WIDTH    = 8;
56  parameter POINTER_WIDTH = 3;
57  //
58  `ifndef SET_HIGH_EN
59    parameter TH_LEVEL  = (2**POINTER_WIDTH)/2;
60  `else
61    `ifndef SET_LOW_EN
62       parameter TH_LEVEL  = (2**POINTER_WIDTH)/2;
63    `endif
64  `endif
65  parameter DATA_NUM      = 2**POINTER_WIDTH;
66  //
67  //inputs
68  `ifdef TWO_CLOCK
69    input clk_rd;
70    input clk_wr;
71  `else
72    input clk;
73  `endif
74  `ifdef SET_LOW_EN
75    input [POINTER_WIDTH:0] low_th;
76  `endif
77  `ifdef SET_HIGH_EN
78    input [POINTER_WIDTH:0] high_th;
79  `endif
80  input rst_n;
81  input wr;
82  input rd;
83  input [DATA_WIDTH-1:0] data_in;
84  //outputs
85  output  reg [DATA_WIDTH-1:0] data_out;
86  `ifdef ALMOST_EMPTY_SIGNAL
87    output wire sfifo_almost_empty;
88  `endif
89  `ifdef ALMOST_FULL_SIGNAL
90    output wire sfifo_almost_full;
91  `endif
92  `ifdef OV_SIGNAL
93    output wire sfifo_ov;
94  `endif
95  `ifdef UD_SIGNAL
96    output wire sfifo_ud;
97  `endif
98  `ifdef EMPTY_SIGNAL
99    output  sfifo_empty;
100  `else
101    wire    sfifo_empty;
102  `endif
103  `ifdef FULL_SIGNAL
104    output  sfifo_full;
105  `else
106    wire    sfifo_full;
107  `endif
108  //internal signals
109  reg [POINTER_WIDTH:0] w_pointer;
110  reg [POINTER_WIDTH:0] r_pointer;
111  reg [DATA_WIDTH-1:0] mem_array [DATA_NUM-1:0];
112  wire    msb_diff;
113  wire    lsb_equal;
114  wire    sfifo_re;
115  wire    sfifo_we;
116  //write pointer
117  assign sfifo_we = wr & (~sfifo_full);
118  //
119  `ifdef TWO_CLOCK
120    wire rclk = clk_rd;
121    wire wclk = clk_wr;
122  `else
123    wire rclk = clk;
124    wire wclk = clk;
125  `endif
126  always @ (posedge wclk or negedge rst_n) begin
127    if (~rst_n) w_pointer <= {POINTER_WIDTH+1{1'b0}};
128    else if (sfifo_we) w_pointer <= w_pointer+1'b1;
129  end
130  //read pointer
131  assign sfifo_re = rd & (~sfifo_empty);
132  //
133  always @ (posedge rclk or negedge rst_n) begin
134    if (~rst_n) r_pointer <= {POINTER_WIDTH+1{1'b0}};
135    else if (sfifo_re) r_pointer <= r_pointer + 1'b1;
136  end
137  //memory array and write decoder
138  always @ (posedge wclk) begin
139    if (sfifo_we)
140      mem_array[w_pointer[POINTER_WIDTH-1:0]]
141               <= data_in[DATA_WIDTH-1:0];
142  end
143  //read decoder
144  `ifdef OUTPUT_REG
145    always @ (posedge rclk) begin
146      if(~rst_n) data_out <= {DATA_WIDTH{1'b0}};
147      // else if(sfifo_re) begin
148      else data_out[DATA_WIDTH-1:0] <= mem_array[r_pointer[POINTER_WIDTH-1:0]];
149      // end
150    end
151  `else
152    always @ (*) begin
153      data_out[DATA_WIDTH-1:0]
154      = mem_array[r_pointer[POINTER_WIDTH-1:0]];
155    end
156  `endif
157  //status signal
158  assign  msb_diff = w_pointer[POINTER_WIDTH]
159                    ^r_pointer[POINTER_WIDTH];
160  //
161  assign  lsb_equal = (w_pointer[POINTER_WIDTH-1:0]
162                    == r_pointer[POINTER_WIDTH-1:0]);
163  //
164  assign  sfifo_full = msb_diff & lsb_equal;
165  //
166  assign  sfifo_empty = (~msb_diff) & lsb_equal;
167  //
168  //Overflow
169  `ifdef OV_SIGNAL
170    reg ov_reg;
171    `ifdef TWO_CLOCK
172      assign sfifo_ov = ov_reg & sfifo_full;
173      wire clr_ov = ~sfifo_full;
174    `else
175      wire clr_ov = sfifo_re;
176      assign sfifo_ov = ov_reg;
177    `endif
178    always @ (posedge wclk) begin
179      if (~rst_n) ov_reg <= 1'b0;
180      else if (clr_ov) ov_reg <= 1'b0;
181      else if (wr & sfifo_full) ov_reg <= 1'b1;
182    end
183  `endif
184  //Underflow
185  `ifdef UD_SIGNAL
186    reg ud_reg;
187    `ifdef TWO_CLOCK
188      assign sfifo_ud = ud_reg & sfifo_empty;
189      wire clr_ud = ~sfifo_empty;
190    `else
191      assign sfifo_ud = ud_reg;
192      wire clr_ud = sfifo_we;
193    `endif
194    always @ (posedge rclk) begin
195      if (~rst_n) ud_reg <= 1'b0;
196      else if (clr_ud) ud_reg <= 1'b0;
197      else if (rd & sfifo_empty) ud_reg <= 1'b1;
198    end
199  `endif
200  //The minus result of two pointers
201  `ifdef ALMOST_EMPTY_SIGNAL
202    wire [POINTER_WIDTH:0] minus_result = w_pointer[POINTER_WIDTH:0] - r_pointer[POINTER_WIDTH:0];
203  `else
204    `ifdef ALMOST_FULL_SIGNAL
205      wire [POINTER_WIDTH:0] minus_result = w_pointer[POINTER_WIDTH:0] - r_pointer[POINTER_WIDTH:0];
206    `endif
207  `endif
208  //The low threshold signal
209  `ifdef ALMOST_EMPTY_SIGNAL
210    `ifdef SET_LOW_EN
211      wire [POINTER_WIDTH:0] low_level = low_th[POINTER_WIDTH:0];
212    `else
213      wire [POINTER_WIDTH:0] low_level = TH_LEVEL;
214    `endif
215  `endif
216  
217  `ifdef ALMOST_EMPTY_SIGNAL
218    assign sfifo_almost_empty = (minus_result[POINTER_WIDTH:0] < low_level[POINTER_WIDTH:0]);
219  `endif
220  //The high threshold signal
221  `ifdef ALMOST_FULL_SIGNAL
222    `ifdef SET_HIGH_EN
223      wire [POINTER_WIDTH:0] high_level = high_th[POINTER_WIDTH:0];
224    `else
225      wire [POINTER_WIDTH:0] high_level = TH_LEVEL;
226    `endif
227  `endif
228  
229  `ifdef ALMOST_FULL_SIGNAL
230    assign sfifo_almost_full = (minus_result[POINTER_WIDTH:0] >= high_level[POINTER_WIDTH:0]);
231  `endif
232  endmodule: sfiforeq
