tt.pre:
1  //===================================================================================
2  // File name:	sfifo.v
3  // Project:	  RTL code generator
4  // Module:    Flexible synchronous FIFO
5  // Function:	Synchronous FIFO with the configuration parameters
6  //===================================================================================
7  //All defines are used to configured the synchronous FIFO before synthesizing
8  //`ifndef EMPTY_SIGNAL
9    //`define EMPTY_SIGNAL
10  //`endif
11  `ifndef FULL_SIGNAL
12    `define FULL_SIGNAL
13  `endif
14  //`define OV_SIGNAL
15  //`define UD_SIGNAL
16  // `define OUTPUT_REG
17  //`define TWO_CLOCK
18  // `define ALMOST_FULL_SIGNAL
19  `define ALMOST_EMPTY_SIGNAL
20  module sfifowdata (rst_n, wr, rd,
21                    `ifdef TWO_CLOCK
22                      clk_rd, clk_wr,
23                    `else
24                      clk,
25                    `endif
26                    `ifdef EMPTY_SIGNAL
27                      sfifo_empty,
28                    `endif
29                    `ifdef FULL_SIGNAL
30                      sfifo_full,
31                    `endif
32                    `ifdef ALMOST_EMPTY_SIGNAL
33                      sfifo_almost_empty,
34                    `endif
35                    `ifdef ALMOST_FULL_SIGNAL
36                      sfifo_almost_full,
37                    `endif
38                    `ifdef OV_SIGNAL
39                      sfifo_ov,
40                    `endif
41                    `ifdef UD_SIGNAL
42                      sfifo_ud,
43                    `endif
44                      data_in, data_out);
45  //Parameters are used to set the capacity of FIFO
46  parameter DATA_WIDTH    = 8;
47  parameter POINTER_WIDTH = 3;
48  parameter [POINTER_WIDTH:0] ALMOST_FULL_VALUE  = (2**POINTER_WIDTH)/2;
49  parameter [POINTER_WIDTH:0] ALMOST_EMPTY_VALUE  = (2**POINTER_WIDTH)/2;
50  parameter DATA_NUM      = 2**POINTER_WIDTH;
51  //
52  //inputs
53  `ifdef TWO_CLOCK
54    input clk_rd;
55    input clk_wr;
56  `else
57    input clk;
58  `endif
59  input rst_n;
60  input wr;
61  input rd;
62  input [DATA_WIDTH-1:0] data_in;
63  //outputs
64  output  reg [DATA_WIDTH-1:0] data_out;
65  `ifdef ALMOST_EMPTY_SIGNAL
66    output wire sfifo_almost_empty;
67  `endif
68  `ifdef ALMOST_FULL_SIGNAL
69    output wire sfifo_almost_full;
70  `endif
71  `ifdef OV_SIGNAL
72    output wire sfifo_ov;
73  `endif
74  `ifdef UD_SIGNAL
75    output wire sfifo_ud;
76  `endif
77  `ifdef EMPTY_SIGNAL
78    output  sfifo_empty;
79  `else
80    wire    sfifo_empty;
81  `endif
82  `ifdef FULL_SIGNAL
83    output  sfifo_full;
84  `else
85    wire    sfifo_full;
86  `endif
87  //internal signals
88  reg [POINTER_WIDTH:0] w_pointer;
89  reg [POINTER_WIDTH:0] r_pointer;
90  reg [DATA_WIDTH-1:0] mem_array [DATA_NUM-1:0];
91  wire    msb_diff;
92  wire    lsb_equal;
93  wire    sfifo_re;
94  wire    sfifo_we;
95  //write pointer
96  assign sfifo_we = wr & (~sfifo_full);
97  //
98  `ifdef TWO_CLOCK
99    wire rclk = clk_rd;
100    wire wclk = clk_wr;
101  `else
102    wire rclk = clk;
103    wire wclk = clk;
104  `endif
105  always @ (posedge wclk or negedge rst_n) begin
106    if (~rst_n) w_pointer <= {POINTER_WIDTH+1{1'b0}};
107    else if (sfifo_we) w_pointer <= w_pointer+1'b1;
108  end
109  //read pointer
110  assign sfifo_re = rd & (~sfifo_empty);
111  //
112  always @ (posedge rclk or negedge rst_n) begin
113    if (~rst_n) r_pointer <= {POINTER_WIDTH+1{1'b0}};
114    else if (sfifo_re) r_pointer <= r_pointer + 1'b1;
115  end
116  //memory array and write decoder
117  always @ (posedge wclk) begin
118    if (sfifo_we)
119      mem_array[w_pointer[POINTER_WIDTH-1:0]]
120               <= data_in[DATA_WIDTH-1:0];
121  end
122  //read decoder
123  `ifdef OUTPUT_REG
124    always @ (posedge rclk) begin
125      if(~rst_n) data_out <= {DATA_WIDTH{1'b0}};
126      // else if(sfifo_re) begin
127      else data_out[DATA_WIDTH-1:0] <= mem_array[r_pointer[POINTER_WIDTH-1:0]];
128      // end
129    end
130  `else
131    always @ (*) begin
132      data_out[DATA_WIDTH-1:0]
133      = mem_array[r_pointer[POINTER_WIDTH-1:0]];
134    end
135  `endif
136  //status signal
137  assign  msb_diff = w_pointer[POINTER_WIDTH]
138                    ^r_pointer[POINTER_WIDTH];
139  //
140  assign  lsb_equal = (w_pointer[POINTER_WIDTH-1:0]
141                    == r_pointer[POINTER_WIDTH-1:0]);
142  //
143  assign  sfifo_full = msb_diff & lsb_equal;
144  //
145  assign  sfifo_empty = (~msb_diff) & lsb_equal;
146  //
147  //Overflow
148  `ifdef OV_SIGNAL
149    reg ov_reg;
150    `ifdef TWO_CLOCK
151      assign sfifo_ov = ov_reg & sfifo_full;
152      wire clr_ov = ~sfifo_full;
153    `else
154      wire clr_ov = sfifo_re;
155      assign sfifo_ov = ov_reg;
156    `endif
157    always @ (posedge wclk) begin
158      if (~rst_n) ov_reg <= 1'b0;
159      else if (clr_ov) ov_reg <= 1'b0;
160      else if (wr & sfifo_full) ov_reg <= 1'b1;
161    end
162  `endif
163  //Underflow
164  `ifdef UD_SIGNAL
165    reg ud_reg;
166    `ifdef TWO_CLOCK
167      assign sfifo_ud = ud_reg & sfifo_empty;
168      wire clr_ud = ~sfifo_empty;
169    `else
170      assign sfifo_ud = ud_reg;
171      wire clr_ud = sfifo_we;
172    `endif
173    always @ (posedge rclk) begin
174      if (~rst_n) ud_reg <= 1'b0;
175      else if (clr_ud) ud_reg <= 1'b0;
176      else if (rd & sfifo_empty) ud_reg <= 1'b1;
177    end
178  `endif
179  //The minus result of two pointers
180  `ifdef ALMOST_EMPTY_SIGNAL
181    wire [POINTER_WIDTH:0] minus_result = w_pointer[POINTER_WIDTH:0] - r_pointer[POINTER_WIDTH:0];
182  `else
183    `ifdef ALMOST_FULL_SIGNAL
184      wire [POINTER_WIDTH:0] minus_result = w_pointer[POINTER_WIDTH:0] - r_pointer[POINTER_WIDTH:0];
185    `endif
186  `endif
187  //The low threshold signal
188  //`ifdef ALMOST_EMPTY_SIGNAL
189    //`ifdef SET_LOW_EN
190      //wire [POINTER_WIDTH:0] low_level = low_th[POINTER_WIDTH:0];
191    //`else
192      //wire [POINTER_WIDTH:0] low_level = TH_LEVEL;
193    //`endif
194  //`endif
195  
196  `ifdef ALMOST_EMPTY_SIGNAL
197    assign sfifo_almost_empty = (minus_result[POINTER_WIDTH:0] < ALMOST_EMPTY_VALUE);//low_level[POINTER_WIDTH:0]);
198  `endif
199  ////The high threshold signal
200  //`ifdef ALMOST_FULL_SIGNAL
201    //`ifdef SET_HIGH_EN
202      //wire [POINTER_WIDTH:0] high_level = high_th[POINTER_WIDTH:0];
203    //`else
204      //wire [POINTER_WIDTH:0] high_level = TH_LEVEL;
205    //`endif
206  //`endif
207  
208  `ifdef ALMOST_FULL_SIGNAL
209    assign sfifo_almost_full = (minus_result[POINTER_WIDTH:0] >= ALMOST_FULL_VALUE);//high_level[POINTER_WIDTH:0]);
210  `endif
211  endmodule: sfifowdata
